import React,{useEffect} from 'react'
import { Row, Col, Card, Button, Image,ListGroup, Form} from 'react-bootstrap'
import { Link} from 'react-router-dom'
import { useDispatch,useSelector} from 'react-redux'
import Message from '../components/Message'
import {addToCart, removeFromCart} from '../actions/cartAction'

const CartScreen = ({match, location, history}) => {
    const dispatch=useDispatch()
    const productId=match.params.id

    const cart=useSelector(state=>state.cart)
    const {cartItems}=cart

    const qty=location.search?Number(location.search.split('=')[1]):1
    useEffect(()=>{
        if(productId){
            dispatch(addToCart(productId,qty))
        }
    },[dispatch,productId,qty])

    const removeFromCartHandler=(id)=>{
        dispatch(removeFromCart(id))
    }

    const checkoutHandler=()=>{
        history.push('/login?redirect=shipping')
    }

    return (
        <Row>
            <Col md={8}>
                <h1>Shopping Cart</h1>
                {cartItems.length===0?(
                    <Message>
                        Your cart is Empty <Link to='/'>Go Back</Link>
                    </Message>
                )
                :(
                    <ListGroup variant='flush'>
                        {cartItems.map(item=>(
                            <ListGroup.Item key={item.product}>
                                <Row>
                                    <Col md={2}>
                                        <Image src={item.image} alt={item.name} fluid rounded/>
                                    </Col>
                                    <Col md={3}>
                                        <Link to={`product/${item.product}`}>
                                            {item.name}
                                        </Link>
                                    </Col>
                                    <Col md={2}>
                                            ${item.price}
                                    </Col>
                                    <Col md={2}>
                                    <Form.Control as='select' value={item.qty} onChange={e=>{
                                    dispatch(addToCart(item.product,Number(e.target.value)))
                                }}>
                                    {[...Array(item.countInStock).keys()].map(e=>
                                    (
                                       <option key={e+1} value={e+1}>
                                           {e+1}
                                       </option> 
                                    )
                                    )}
                                </Form.Control>
                                    </Col>
                                    <Col md={2}>
                                        <Button onClick={()=>{removeFromCartHandler(item.product)}} type='button' variant='light'>
                                        <i className='fas fa-trash'></i>
                                        </Button>
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                )
            }
            </Col>
            <Col md={4}>
                <Card>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                            <h2>Sub Total ({cartItems.reduce((acc,item)=>acc+item.qty,0)})</h2>
                            ${cartItems.reduce((acc,item)=>acc+item.qty*item.price,0).toFixed(2)}
                        </ListGroup.Item>
                        <ListGroup.Item>
                            <Button type='button' className='btn-block' disabled={cartItems.length===0} onClick={checkoutHandler}>
                                Proceed To Checkout
                            </Button>
                        </ListGroup.Item>
                    </ListGroup>
                </Card>
            </Col>
        </Row>
    )
}

export default CartScreen
